import React, { Component } from "react";
import { updateBook, addBook } from "../../actions/bookActions";
import { connect } from "react-redux";

class ManageBook extends Component {
  state = { ...this.props.currentBook };

  handleChange = event => {
    let { name, value } = event.target;
    this.setState({
      ...this.state,
      [name]: value
    });
  };

  render() {
    let book = this.props.currentBook;
    let authors = this.props.authors.map(author => (
      <option value={author.id} selected={book.authorId === author.id}>
        {author.name}
      </option>
    ));
    return (
      <form>
        <label htmlFor="bookId">Book Id:</label>
        <input
          type="text"
          value={book.id}
          onChange={this.handleChange}
          name="bookId"
        />
        <br />
        <label htmlFor="bookName">Book Name:</label>
        <input
          type="text"
          value={book.name}
          onChange={this.handleChange}
          name="bookName"
        />
        <select value={book.authorId}>{authors}</select>
      </form>
    );
  }
}

function mapStateToProps(state, ownProps) {
  const bookId = parseInt(ownProps.match.params.id);

  let currentBook = {
    bookId: 0,
    bookName: "",
    authorId: 0
  };

  if (bookId !== 0 && state.books.length > 0) {
    currentBook = state.books.find(book => book.id === bookId) || currentBook;
  }

  return {
    currentBook: currentBook,
    books: state.books,
    authors: state.authors
  };
}

const mapDispatchToProps = {
  updateBook,
  addBook
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ManageBook);
