import React from "react";
import { connect } from "react-redux";
import * as bookActions from "../../actions/bookActions";
import PropTypes from "prop-types";
import { Link, Redirect } from "react-router-dom";

class Books extends React.Component {
  componentDidMount() {
    if (!this.props.books || this.props.books.length === 0) {
      this.props.loadBooks();
    }
  }

  render() {
    let books = this.props.books.map(book => (
      <span key={book.id}>
        <Link to={`/book/${book.id}`}>{book.name}</Link>
        <button onClick={() => this.props.deleteBook(book)}>Delete</button>
        <br />
      </span>
    ));
    return (
      <div>
        <h1>Books</h1>
        <Link to="/book/0" className="btn btn-primary">
          Add book
        </Link>
        <br />
        {books}
      </div>
    );
  }
}

Books.propTypes = {
  books: PropTypes.array.isRequired,
  authors: PropTypes.array.isRequired,
  apiCallInProgress: PropTypes.bool.isRequired,
  loadBooks: PropTypes.func.isRequired,
  deleteBook: PropTypes.func.isRequired
};

function mapStateToProps(state) {
  return {
    books: state.books,
    authors: state.authors,
    apiCallInProgress: state.apiCallInProgress
  };
}

function mapDispatchToProps(dispatch) {
  return {
    loadBooks: () => dispatch(bookActions.loadBooks()),
    deleteBook: book => dispatch(bookActions.deleteBook(book))
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Books);
