import React from "react";
import { connect } from "react-redux";
import * as bookActions from "../../actions/bookActions";
import * as authorActions from "../../actions/authorActions";
import PropTypes from "prop-types";
import { Link, Redirect } from "react-router-dom";

class Authors extends React.Component {
  componentDidMount() {
    if (!this.props.authors || this.props.authors.length === 0) {
      this.props.loadAuthors();
    }
    if (!this.props.books || this.props.books.length === 0) {
      this.props.loadBooks();
    }
  }

  render() {
    let authors = this.props.authors.map(book => (
      <span key={book.id}>
        <Link to={`/book/${book.id}`}>{book.name}</Link>
        <button onClick={() => this.props.deleteBook(book)}>Delete</button>
        <br />
      </span>
    ));
    return (
      <div>
        <h1>Authors</h1>
        <Link to="/book/0" className="btn btn-primary">
          Add book
        </Link>
        <br />
        {authors}
      </div>
    );
  }
}

Authors.propTypes = {
  books: PropTypes.array.isRequired,
  authors: PropTypes.array.isRequired,
  apiCallInProgress: PropTypes.bool.isRequired,
  loadAuthors: PropTypes.func.isRequired,
  deleteAuthor: PropTypes.func.isRequired
};

function mapStateToProps(state) {
  return {
    books: state.books,
    authors: state.authors,
    apiCallInProgress: state.apiCallInProgress
  };
}

function mapDispatchToProps(dispatch) {
  return {
    loadBooks: () => dispatch(bookActions.loadBooks()),
    deleteAuthor: author => dispatch(bookActions.deleteAuthor(author)),
    loadAuthors: () => dispatch(authorActions.loadAuthors())
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Authors);
