import React, { Component } from "react";
import { updateBook, addBook } from "../../actions/bookActions";
import { connect } from "react-redux";

class ManageBook extends Component {
  handleChange = event => {
    console.log(event.target);
  };
  render() {
    let book = this.props.currentBook;

    return (
      <form>
        <input
          type="text"
          value={book.id}
          onChange={this.handleChange}
          fieldName="id"
        />
        <input
          type="text"
          value={book.name}
          onChange={this.handleChange}
          fieldName="name"
        />
      </form>
    );
  }
}

function mapStateToProps(state, ownProps) {
  const bookId = ownProps.match.params.id;

  let currentBook = {
    bookId: 0,
    bookName: "",
    authorId: 0
  };

  if (bookId && state.books.length > 0) {
    currentBook = state.books.find(book => book.id === bookId) || currentBook;
  }

  return {
    currentBook: currentBook,
    books: state.books,
    authors: state.authors
  };
}

const mapDispatchToProps = {
  updateBook,
  addBook
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ManageBook);
