import React, { Component } from "react";
import { updateBook, addBook } from "../../actions/bookActions";
import { connect } from "react-redux";

class ManageBook extends Component {
  state = { ...this.props.currentBook };

  handleChange = event => {
    let { name, value } = event.target;
    this.setState({
      ...this.state,
      [name]: value
    });
  };

  render() {
    let book = this.props.currentBook;

    return (
      <form>
        <input
          type="text"
          value={book.id}
          onChange={this.handleChange}
          name="bookId"
        />
        <input
          type="text"
          value={book.name}
          onChange={this.handleChange}
          name="bookName"
        />
      </form>
    );
  }
}

function mapStateToProps(state, ownProps) {
  const bookId = ownProps.match.params.id;
  console.log(bookId);
  let currentBook = {
    bookId: 0,
    bookName: "",
    authorId: 0
  };
  console.log(JSON.stringify(state.books));
  if (bookId !== 0 && state.books.length > 0) {
    currentBook = state.books.find(book => {
      if (book.id === bookId) {
        return book;
      }
    });
  }
  console.log(JSON.stringify(currentBook));

  return {
    currentBook: currentBook,
    books: state.books,
    authors: state.authors
  };
}

const mapDispatchToProps = {
  updateBook,
  addBook
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ManageBook);
